const message = $input.first()?.json?.message;
const callbackData = $input.first()?.json?.callback_query?.data || '';
const inputText = message?.text || '';

const extractAnswers = (text, callback) => {
  const answers = {
    name: '',
    age: 0,
    gender: '',
    prefered_topic: '',
    object: '',
    problem_topic: '',
    style: ''
  };

  // –†–∞–∑–¥–µ–ª—è–µ–º —Ç–µ–∫—Å—Ç –ø–æ —Å—Ç—Ä–æ–∫–∞–º
  const lines = text.split('\n').map(line => line.trim()).filter(line => line);

  // –ú–∞–ø–ø–∏–Ω–≥ —Å—Ç—Ä–æ–∫ –Ω–∞ –ø–æ–ª—è
  if (lines.length >= 7) {
    answers.name = lines[0] || ''; // –ü–∞–≤–µ–ª
    answers.age = parseInt(lines[1]) || 0; // 4
    answers.gender = lines[2]?.toLowerCase().includes('–º–∞–ª—å—á–∏–∫') ? '–ú' : (lines[2]?.toLowerCase().includes('–¥–µ–≤–æ—á–∫–∞') ? '–ñ' : ''); // –º–∞–ª—å—á–∏–∫
    answers.prefered_topic = lines[3]?.trim() || ''; // –¥–∏–Ω–æ–∑–∞–≤—Ä–æ–≤
    answers.object = lines[4]?.replace(/—Ö–æ—á–µ—Ç\s+—Å–∫–∞–∑–∫—É\s+–ø—Ä–æ\s+/i, '').trim() || ''; // –¥–∏–Ω–æ–∑–∞–≤—Ä–∞ –ï–≤—Ä–∏–∫–∞
    answers.problem_topic = lines[5]?.replace(/(?:–±–æ—è–∑–Ω—å|—Å—Ç—Ä–∞—Ö)\s+/i, '').trim() || ''; // —Ç–µ–º–Ω–æ—Ç—ã
    answers.style = lines[6]?.trim() || ''; // —É—Å–ø–æ–∫–∞–∏–≤–∞—é—â–∏–π
  }

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ callback_data (–¥–ª—è –∏–Ω–ª–∞–π–Ω-–∫–Ω–æ–ø–æ–∫)
  if (callback) {
    const [key, value] = callback.split(':');
    if (key === 'gender') answers.gender = value;
    if (key === 'style') answers.style = value;
  }

  return answers;
};

const answers = extractAnswers(inputText, callbackData);

return [{
  json: {
    user_id: message?.from?.id || "0",
    chat_id: message?.chat?.id || "0",
    child_name: answers.name,
    age: answers.age,
    gender: answers.gender,
    prefered_topic: answers.prefered_topic,
    object: answers.object,
    problem_topic: answers.problem_topic,
    style: answers.style,
    creation_date: new Date().toISOString(),
    raw_text: inputText,
    debug: JSON.stringify({ message, callbackData, answers, lines: inputText.split('\n') })
  }
}];

function cleanAndExtractJSON(response) {
    try {
        const result = {
            image_prompt: []
        };

        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ choices –∏ content
        if (response.choices && response.choices[0] && response.choices[0].message && response.choices[0].message.content) {
            result.image_prompt.push(response.choices[0].message.content.trim());
        } else {
            result.image_prompt.push("Default prompt"); // –§allback
        }

        return { json: result };
    } catch (error) {
        return { 
            json: {
                image_prompt: ["Default prompt"]
            }
        };
    }
}

const response = $input.first().json;
return cleanAndExtractJSON(response);

